import numpy as np

def add_classification_bit(folder, bit):
    # save a file with the classification bit (npy)
    classification_bit = np.array([bit])
    np.save(folder + "/classification_bit.npy", classification_bit)

for folder in spam_list:
    add_classification_bit(folder, 1)

for folder in Pineapple_list:
    add_classification_bit(folder, 2)

for folder in empty_list:
    add_classification_bit(folder, 0)
